client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Smooth scroll behavior */
html {
  scroll-behavior: smooth;
}

/* Scroll animations */
.scroll-animate {
  opacity: 0;
  transform: translateY(30px);
  transition: opacity 0.8s ease-out, transform 0.8s ease-out;
}

.scroll-animate.visible {
  opacity: 1;
  transform: translateY(0);
}

.scroll-animate-fade {
  opacity: 0;
  transition: opacity 1s ease-out;
}

.scroll-animate-fade.visible {
  opacity: 1;
}

.scroll-animate-slide-left {
  opacity: 0;
  transform: translateX(-50px);
  transition: opacity 0.8s ease-out, transform 0.8s ease-out;
}

.scroll-animate-slide-left.visible {
  opacity: 1;
  transform: translateX(0);
}

.scroll-animate-slide-right {
  opacity: 0;
  transform: translateX(50px);
  transition: opacity 0.8s ease-out, transform 0.8s ease-out;
}

.scroll-animate-slide-right.visible {
  opacity: 1;
  transform: translateX(0);
}

.scroll-animate-scale {
  opacity: 0;
  transform: scale(0.9);
  transition: opacity 0.8s ease-out, transform 0.8s ease-out;
}

.scroll-animate-scale.visible {
  opacity: 1;
  transform: scale(1);
}

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;

  --foreground: 222 15% 12%;

  --border: 220 13% 88%;

  --card: 220 13% 98%;

  --card-foreground: 222 15% 12%;

  --card-border: 220 13% 94%;

  --sidebar: 220 13% 96%;

  --sidebar-foreground: 222 15% 12%;

  --sidebar-border: 220 13% 92%;

  --sidebar-primary: 222 85% 38%;

  --sidebar-primary-foreground: 220 15% 98%;

  --sidebar-accent: 220 18% 90%;

  --sidebar-accent-foreground: 222 15% 18%;

  --sidebar-ring: 222 85% 38%;

  --popover: 220 13% 93%;

  --popover-foreground: 222 15% 12%;

  --popover-border: 220 13% 89%;

  --primary: 222 85% 38%;

  --primary-foreground: 220 15% 98%;

  --secondary: 220 13% 89%;

  --secondary-foreground: 222 15% 18%;

  --muted: 220 15% 91%;

  --muted-foreground: 222 12% 35%;

  --accent: 220 18% 92%;

  --accent-foreground: 222 15% 18%;

  --destructive: 0 84% 42%;

  --destructive-foreground: 0 15% 98%;

  --input: 220 13% 78%;
  --ring: 222 85% 38%;
  --chart-1: 222 85% 38%;
  --chart-2: 195 78% 42%;
  --chart-3: 280 65% 48%;
  --chart-4: 45 88% 52%;
  --chart-5: 160 68% 45%;

  --font-sans: Inter, system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'Space Mono', Menlo, monospace;
  --radius: .75rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(220 13% 10% / 0.04);
  --shadow-xs: 0px 1px 2px 0px hsl(220 13% 10% / 0.06);
  --shadow-sm: 0px 1px 3px 0px hsl(220 13% 10% / 0.08), 0px 1px 2px -1px hsl(220 13% 10% / 0.08);
  --shadow: 0px 2px 4px -1px hsl(220 13% 10% / 0.06), 0px 4px 6px -1px hsl(220 13% 10% / 0.10);
  --shadow-md: 0px 4px 6px -1px hsl(220 13% 10% / 0.08), 0px 2px 4px -2px hsl(220 13% 10% / 0.08);
  --shadow-lg: 0px 10px 15px -3px hsl(220 13% 10% / 0.10), 0px 4px 6px -4px hsl(220 13% 10% / 0.10);
  --shadow-xl: 0px 20px 25px -5px hsl(220 13% 10% / 0.10), 0px 8px 10px -6px hsl(220 13% 10% / 0.10);
  --shadow-2xl: 0px 25px 50px -12px hsl(220 13% 10% / 0.25);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 222 15% 8%;

  --foreground: 220 15% 95%;

  --border: 220 13% 18%;

  --card: 222 15% 10%;

